public class ArraysFour {	public static void main(String[] args) {		int[] four = new int[10];		int[] five = new int[10];		int i;		for(i = 0; i < four.length; i++) {			four[i] = (int)(Math.random()*100+1);		}		for(i = 0; i < four.length; i++) {			five[i] = four[i];		}				four[four.length-1] = -7;				System.out.print("Array 0: ");		for (i = 0; i < four.length; i++) {			System.out.print(four[i] + " ");		}				System.out.print("\nArray 1: ");		for (i = 0; i < five.length; i++) {			System.out.print(five[i] + " ");		}				System.out.println();	}}