/* Assignment from Graham Mitchell: * https://programmingbydoing.com/a/basic-arrays-3.html *  * Create an array that can hold 1000 integers. Fill the * array with random numbers in the range 10-99. Then  * display the contents of the array on the screen. You  * must use a loop. */public class ArraysRandom1000 {	public static void main(String[] args) {		int[] third = new int[1000];		int i = 0;		for (i = 0; i < third.length; i++) {			third[i] = (int)(Math.random()*90+10);		}				for (i = 0; i < third.length; i++) {			System.out.print(third[i] + "  ");		}	}}