import java.util.Scanner;public class HighLow {	public static void main(String[] args) {				Scanner keyboard = new Scanner(System.in);				int secret = 1 + (int)(100*Math.random());				System.out.println("I'm thinking of a number between 1 and 100");		System.out.println("Can you guess it?");				int guess = keyboard.nextInt();				while(guess != secret) {						if(guess < secret) {				System.out.print("Too low! Try again: ");				guess = keyboard.nextInt();			}						if(guess > secret) {				System.out.print("Too high! Try again: ");				guess = keyboard.nextInt();			}					}				System.out.println("You got it! Well done!");				}}