import java.io.File;import java.util.Scanner;public class ReadFile {		public static void main(String[] args) throws Exception {			int fourletter = 0;		int caps = 0;				Scanner filename = new Scanner(System.in);		System.out.print("Enter a file name to read: ");		String fn1 = filename.next();				Scanner wordReadr = new Scanner(new File(fn1));				while(wordReadr.hasNext()) {						String w = wordReadr.next();						if (w.length() == 4) {				fourletter++;			}						if (Character.isUpperCase(w.charAt(0))) {				caps++;			}				}				wordReadr.close();				System.out.println(fourletter + " 4-letter words in readoc.txt.");		System.out.println(caps + " capitalized words in readoc.txt.");	}}